xwikidoc.content:
{{groovy}}
import org.xwiki.observation.*
import org.xwiki.observation.event.*
import org.xwiki.bridge.event.*
import org.xwiki.model.reference.*
import org.xwiki.model.*
import com.xpn.xwiki.web.*
import com.xpn.xwiki.*

class RecommendationsDeletedPagesEventListener implements EventListener
{
  String getName()
  {
    // The unique name of this event listener
    return "inactives"
  }

  List<Event> getEvents()
  {
    // The list of events this listener listens to
    return [new DocumentDeletedEvent()]
  }

  // Called by the Observation Manager when an event matches the list of events returned by getEvents()
  void onEvent(Event event, Object source, Object context)
  {
    try {
      def xwiki = context.getWiki()
      def configReference = new EntityReference("RecEngineConfig", EntityType.DOCUMENT, 
        new EntityReference("RecommendationsCode", EntityType.SPACE))
      // Prevent sending profiles for blacklisted spaces
      def configObject = xwiki.getDocument(configReference , context).getObject('RecommendationsCode.RecEngineConfigClass')
      def easyrecAddress = ""
      if(configObject.get('easyrecInternalAddress').value) {
        easyrecAddress = configObject.get('easyrecInternalAddress').value
      }
      else {
        easyrecAddress = configObject.get('easyrecServerAddress').value
      }
      def easyrecApiKey = configObject.get('easyrecApiKey').value
      def easyrecTenantId = configObject.get('easyrecTenantId').value
      def queryURL = easyrecAddress+'api/1.0/setitemactive?active=false&apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&itemid='+URLEncoder.encode(source.fullName, "UTF-8");
      def deletePage = xwiki.getURLContent(queryURL, context)
    } catch (Exception e) {
      System.out.println("DELETED PAGES LISTENER: Exception "+e)
    }
  }
}

def observation = Utils.getComponent(ObservationManager.class)
if(request.get('enable')) {
  observation.removeListener("inactives")
  def listener = new RecommendationsDeletedPagesEventListener()
  observation.addListener(listener)
}
else if(request.get('disable')) {
  observation.removeListener("inactives")
}

if(observation.getListener("inactives")) {
  println "Listener **enabled**"
  println "[[Disable the listener>>RecommendationsCode.DeletedPagesListener||queryString='disable=1']]"
}
else {
  println "Listener **disabled**"
  println "[[Enable the listener>>RecommendationsCode.DeletedPagesListener||queryString='enable=1']]"
}
{{/groovy}}
