  def easyrecTenantId = recConfigDoc.get('easyrecTenantId');
  if(easyrecAddress.isEmpty() || easyrecApiKey.isEmpty() || easyrecTenantId.isEmpty()) {
    System.out.println("PUSH PROFILE: wrong easyrec server configuration in XWiki")
    print "<state>0</state>"
    success = false
  }
  def nbre = 0
  def pushedDoc = xwiki.getDocument(request.doc)
  def success = true
  if(!request.get("doc") || !xwiki.exists(request.get("doc"))) {
    System.out.println("PUSH PROFILE: No document")
    print "<state>0</state>"
    success = false;
  }
  else {
    System.out.println("PUSH PROFILE: Start pushing data from ${pushedDoc}")

    def pageContentObj = xwiki.parseGroovyFromPage("RecommendationsCode.GetProfileContent")
    def title = pushedDoc.getName();
    def tags = pushedDoc.getTags().toString().replaceAll("\\|", " ");
    def contentList = pageContentObj.getContentFromDoc(pushedDoc.fullName, xwiki).toString().replaceAll("(\\[|\\]|,)", ""); 
    def author = pushedDoc.getAuthor();
    def space = pushedDoc.space;

    def profile = URLEncoder.encode("<profile><title>${title}</title><tags>${tags}</tags><author>${author}</author><space>${space}</space><content>${contentList}</content></profile>", "UTF-8");
    def queryURLItem = "${easyrecAddress}api/1.0/sendaction?actiontype=TEST&apikey=${easyrecApiKey}&tenantid=${easyrecTenantId}&itemdescription=${title}&itemid=${pushedDoc}&itemurl=/xwiki/bin/view/${pushedDoc.space}/${pushedDoc.name}&sessionid=fakesessionidtocreateitems";
    def queryURL = "${easyrecAddress}api/1.0/profile/store?apikey=${easyrecApiKey}&tenantid=${easyrecTenantId}&itemid=${pushedDoc}&profile=${profile}";
    def createItem = xwiki.getURLContent(queryURLItem);
    def sendProfile = xwiki.getURLContent(queryURL);
  }

  if(success) {
    print "<state>1</state>"
  }

  }
  catch(e){
    System.out.println("PUSH PROFILE: Exception")
    e.printStackTrace();
    print "<state>-1</state>"
    print "<error>${e.message}</error>"
  }
    print "</base>"
System.out.println("PUSH PROFILE: Done")
