xwikidoc.content:
{{groovy}}
try {
  def recConfigDoc = xwiki.getDocument('RecommendationsCode.RecEngineConfig')
  def easyrecAddress = ""
  if(recConfigDoc.get('easyrecInternalAddress')) {
    easyrecAddress = recConfigDoc.get('easyrecInternalAddress')
  }
  else {
    easyrecAddress = recConfigDoc.get('easyrecServerAddress')
  }
  def easyrecApiKey = recConfigDoc.get('easyrecApiKey')
  def easyrecTenantId = recConfigDoc.get('easyrecTenantId')
  if(easyrecAddress.isEmpty() || easyrecApiKey.isEmpty() || easyrecTenantId.isEmpty()) {
    System.out.println("PUSH PROFILE: wrong easyrec server configuration in XWiki")
  }
  def pushedDoc = xwiki.getDocument(request.doc)
  if(!request.get("doc") || !xwiki.exists(request.get("doc"))) {
    System.out.println("PUSH PROFILE: No document")
  }
  else {
    def pageContentObj = xwiki.parseGroovyFromPage("RecommendationsCode.GetProfileContent")
    def title = pushedDoc.getName()
    def tags = pushedDoc.getTags().toString().replaceAll("\\|", " ")
    def contentList = pageContentObj.getContentFromDoc(pushedDoc.fullName, xwiki).toString().replaceAll("(\\[|\\]|,)", "")
    def author = pushedDoc.getAuthor()
    def space = pushedDoc.getSpace()
    def docDescription = URLEncoder.encode(pushedDoc.getName(), "UTF-8")
    def docFullName = URLEncoder.encode(pushedDoc.getFullName(), "UTF-8")
    def docName = URLEncoder.encode(pushedDoc.getName(), "UTF-8")
    def docSpace = URLEncoder.encode(pushedDoc.getSpace(), "UTF-8")

    def profile = URLEncoder.encode("<profile><tags>${tags}</tags><author>${author}</author><space>${docSpace}</space><content>${contentList}</content></profile>", "UTF-8")
    def queryURLItem = "${easyrecAddress}api/1.0/sendaction?actiontype=CREATE&apikey=${easyrecApiKey}&tenantid=${easyrecTenantId}&itemdescription=${docDescription}&itemid=${docFullName}&itemurl=/xwiki/bin/view/${docSpace}/${docName}&sessionid=fakesessionidtocreateitems"
    def queryURL = "${easyrecAddress}api/1.0/profile/store?apikey=${easyrecApiKey}&tenantid=${easyrecTenantId}&itemid=${docFullName}&profile=${profile}"
    def createItem = xwiki.getURLContent(queryURLItem)
    def sendProfile = xwiki.getURLContent(queryURL)
  }
}
catch(e){
  System.out.println("PUSH PROFILE: Exception "+e)
}
{{/groovy}}
