property.code:
require(['jquery'], function ($) {
  $(function(){

    // easyrec configuration
    var resultDataElement = $('.recommendations-data').html();
    var resultData = JSON.parse(decodeURIComponent(resultDataElement).replace(/\+/g, ' '));
    var easyrecAddress = resultData.easyrecServerAddress;
    var easyrecApiKey = resultData.easyrecApiKey;
    var easyrecTenantId = resultData.easyrecTenantId;
    var sessionId = resultData.sessionId;
    var userId = resultData.userId;
    var userBasedState = resultData.userBasedState;
    var contentBasedState = resultData.contentBasedState;
    var idList = [[],[],[],[]];
    var panelDiv = $('#recommendationsList');

    if(!easyrecAddress.empty() && !easyrecApiKey.empty() && !easyrecTenantId.empty()) {
    $.when(
      otherUsersCall(),
      contentBasedCall(),
      viewActionCall(),
      editActionCall()
    ).then(function(jqxhr1, jqxhr2, jqxhr3, jqxhr4) {
      var data = new Array();
      data[0] = jqxhr1[0];
      data[1] = jqxhr2[0];
      data[2] = jqxhr3[0];
      data[3] = jqxhr4[0];

      for(var i=0; i<=3; i++) {
        var j=1;
        if(data[i] && data[i].recommendeditems) {
          if($.isArray(data[i].recommendeditems.item)) { // .each() won't work if it isn't an array (one element only)
            data[i].recommendeditems.item.each(function(value) {
              if(j<=8 && $.inArray(value.id, idList[0])<0 && $.inArray(value.id, idList[1])<0 && $.inArray(value.id, idList[2])<0) {
                idList[i].push(value.id);  // Push "edit" recommendations to the list
                j++;
              }
            });
          }
          else {
            idList[i].push(data[i].recommendeditems.item.id);
          }
        }
      }
      $.ajax({
        dataType : 'html',
        url : '/xwiki/bin/get/RecommendationsCode/DisplayPrettyLinks',
        method : 'GET',
        data : 'pagesList1='+encodeURIComponent(idList[0])+'&pagesList2='+encodeURIComponent(idList[1])+'&pagesList3='+encodeURIComponent(idList[2])+'&pagesList4='+encodeURIComponent(idList[3]),
        success : function(data, status) {
          panelDiv.html(data);
        }
      });
    });
    }
    else {
      panelDiv.html('<em>You have to set up an easyrec server to use this extension</em>');
    }

    function otherUsersCall() {
      if(userBasedState == 1) return $.ajax({
        dataType: 'jsonp',
        url: easyrecAddress+'api/1.0/json/otherusersalsoviewed?apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&itemid='+XWiki.currentSpace+'.'+XWiki.currentPage,
      });
      else return [[]];
    }
    function contentBasedCall() {
      if(contentBasedState == 1) return $.ajax({
        dataType: 'jsonp',
        url: easyrecAddress+'api/1.0/json/relateditems?assoctype=PROFILE_SIMILARITY&apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&itemid='+XWiki.currentSpace+'.'+XWiki.currentPage,
      });
      else return [[]];
    }
    function viewActionCall() {
      if(userBasedState == 1) return $.ajax({
        dataType: 'jsonp',
        url: easyrecAddress+'api/1.0/json/recommendationsforuser?actiontype=VIEW&apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&userid='+userId,
      });
      else return [[]];
    }
    function editActionCall() {
      if(userBasedState == 1) return $.ajax({
        dataType: 'jsonp',
        url: easyrecAddress+'api/1.0/json/recommendationsforuser?numberOfResults=1&actiontype=EDIT&apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&userid='+userId,
      });
      else return [[]];
    }

  });
});
