property.code:
require(['jquery'], function ($) {
  $(function(){

    // easyrec configuration
    var userId = '$xcontext.getUser()';
    var easyrecAddress = '$xwiki.getDocument('RecommendationsCode.RecEngineConfig').get('easyrecServerAddress')';
    var easyrecApiKey = '$xwiki.getDocument('RecommendationsCode.RecEngineConfig').get('easyrecApiKey')';
    var easyrecTenantId = '$xwiki.getDocument('RecommendationsCode.RecEngineConfig').get('easyrecTenantId')';
    var idList = [];
    var splitId = [];
    var panelDiv = $('#recommendationsList');

    if(!easyrecAddress.empty() && !easyrecApiKey.empty() && !easyrecTenantId.empty()) { 
      // "view" actions
      $.ajax({
        dataType: 'jsonp',
        url: easyrecAddress+'api/1.0/json/recommendationsforuser?actiontype=VIEW&apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&userid='+userId,
        timeout: 2000,
        success : function(data, status) {
          if(data.recommendeditems) {
            if($.isArray(data.recommendeditems.item)) { // .each() won't work if it isn't an array (one element only)
              data.recommendeditems.item.each(function(value) {
                idList.push(value.id); // Push "view" recommendations to the list
              });
            }
            else {
              idList.push(data.recommendeditems.item.id);
            }
          }
        },
        complete : function(jqXHR, textStatus) {
          // "edit" actions
          $.ajax({
            dataType: 'jsonp',
            url: easyrecAddress+'api/1.0/json/recommendationsforuser?numberOfResults=1&actiontype=EDIT&apikey='+easyrecApiKey+'&tenantid='+easyrecTenantId+'&userid='+userId,
            timeout: 2000,
            success : function(data) {
              if(data.recommendeditems) { // .each() won't work if it isn't an array (one element only)
                if($.isArray(data.recommendeditems.item)) {
                  data.recommendeditems.item.each(function(value) {
                    if(!($.inArray(idList, value.id))) {
                      idList.push(value.id);  // Push "edit" recommendations to the list
                    }
                  });
                }
                else {
                  idList.push(data.recommendeditems.item.id);
                }
              }
            },
            complete : function (XHR, status) { // Display the results
              if(textStatus == "success" || status == "success") {
                var listSize = idList.length;
                if(listSize == 0) {
                  panelDiv.html('<em>No result</em>');
                }
                else {
                  var index = 0;
                  var randomPagesList = [];
                  for (var i=0; i<listSize ; i++) {
                    index = Math.floor(Math.random()*idList.length);
                    randomPagesList.push(idList.splice(index, 1));
                  }
                  $.ajax({
                    dataType : 'html',
                    url : '/xwiki/bin/get/RecommendationsCode/DisplayPrettyLinks',
                    method : 'POST',
                    data : 'pagesList='+randomPagesList,
                    success : function(data, status) {
                      panelDiv.html(data);
                    }
                  });
                }
              }
              else if(textStatus != "success" && status != "success") {
                panelDiv.html('<em>Unable to reach easyrec server : '+textStatus+'</em>');
              }
            }
          });
        }
      });
    }
    else {
      panelDiv.html('<em>You have to set up an easyrec server to use this extension</em>');
    }

  });
});
